import React, { useState } from 'react';
import {
  TableHead, TextField, 
  IconButton, Paper, TableRow, 
  TablePagination, TableFooter, TableContainer, 
  TableCell, TableBody, Table, Button
} from '@material-ui/core';
import { MdSave, MdCancel } from 'react-icons/md';
import ColorPicker  from 'rc-color-picker';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import NumberFormat from 'react-number-format';

const CategoryModal = ({ open, handleClose, saveCategory }) => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [color, setColor] = useState('');

  const choisingColor = (newColor) => setColor(newColor);

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>Ajouter une nouvelle catégorie</DialogTitle>
      <DialogContent>
        <FormControl fullWidth margin="normal">
          <TextField
            label="Nom de la catégorie"
            variant="outlined"
            value={name}
            onChange={(e) => setName(e.target.value)}
            fullWidth
          />
        </FormControl>
        <FormControl fullWidth margin="normal" variant="outlined">
          <InputLabel htmlFor="formatted-numberformat-input">Prix</InputLabel>
          <OutlinedInput
            label="Prix"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            name="price"
            id="formatted-numberformat-input"
            inputComponent={NumberFormatCustom}
            fullWidth
          />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <ColorPicker onChange={choisingColor} />
        </FormControl>
      </DialogContent>
      <DialogActions>
        <Button onClick={saveCategory} color="primary" variant="contained" startIcon={<MdSave />}>
          Enregistrer
        </Button>
        <Button onClick={handleClose} color="secondary" variant="outlined" startIcon={<MdCancel />}>
          Annuler
        </Button>
      </DialogActions>
    </Dialog>
  );
};

function NumberFormatCustom(props) {
  const { inputRef, onChange, ...other } = props;

  return (
    <NumberFormat
      {...other}
      getInputRef={inputRef}
      onValueChange={(values) => {
        onChange({
          target: {
            name: props.name,
            value: values.value,
          },
        });
      }}
      thousandSeparator
      isNumericString
      prefix="MAD : "
    />
  );
}
