                                                        variant="outlined"
                                                        name="type"
                                                        value={state.nom}
                                                        onChange={handleChange} 
                                                    />
                                                </Col>
                                                <Col xs="12" md="12" className="mt-3" > 
                                                    <TextField
                                                        placeholder=""
                                                        variant="outlined"
                                                        required={false}
                                                        label="description"
                                                        name="description"
                                                        fullWidth
                                                        value={state.description}
                                                        onChange={handleChange} 
                                                    />
                                                
                                                </Col>
                                                <Col xs="12" md="6" className="mt-3" > 
                                                    <Checkbox
