import React, {useState, useEffect} from 'react'
// react plugin for creating charts
// import ChartistGraph from "react-chartist";
// @material-ui/core
import { makeStyles } from "@material-ui/core/styles";
import Icon from "@material-ui/core/Icon";
import {Row, Col, Container} from 'react-bootstrap'
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import MenuItem from '@material-ui/core/MenuItem';

// @material-ui/icons
import Store from "@material-ui/icons/Store";
import Warning from "@material-ui/icons/Warning";
import DateRange from "@material-ui/icons/DateRange";
import LocalOffer from "@material-ui/icons/LocalOffer";
import Update from "@material-ui/icons/Update";

import Accessibility from "@material-ui/icons/Accessibility";

import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";

import Danger from "components/Typography/Danger.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardIcon from "components/Card/CardIcon.js";

import CardFooter from "components/Card/CardFooter.js";

import styles from "assets/jss/views/dashboardStyle.js";
import Consulting from 'assets/img/consultation2.svg'
import imageMoney from 'assets/img/money-img.svg'
import Accessibility2 from 'assets/img/access.svg'
import {FcMoneyTransfer} from 'react-icons/fc'
const useStyles = makeStyles(styles);

export default function Pole() {
  const [categories, setCategories] = useState([]);
  const [categorie, setCategorie] = useState(1);

  const classes = useStyles();
  return (
    <div>
      <GridContainer>
        <div className="text-center" style={{width:"100%",textAlign:"center",alignItems:"center",justifyContent:"center"}}>
        <h1>Trouver un médecin remplaçant</h1>
        </div> 
    
          <div className=".box_general_pole {
">
          <h3>Qualifications</h3>
          <Row>
        
          <Col xs={12} md={4}>
                  <FormControl fullWidth>
                  <Select
                    style={{width: "50%"}}
                    value={categorie}
                    onChange={(event)=> setCategorie(event.target.value)}
                  >
                    {
                      categories.map(categorie => <MenuItem key={categorie.id} value={categorie.id}> {categorie.name} </MenuItem>)
                    }
                  </Select>
                    </FormControl>
                 
            </Col>

            <Col xs={12} md={4}>
                  <FormControl fullWidth>
                  <Select
                    style={{width: "50%"}}
                    value={categorie}
                    onChange={(event)=> setCategorie(event.target.value)}
                  >
                    {
                      categories.map(categorie => <MenuItem key={categorie.id} value={categorie.id}> {categorie.name} </MenuItem>)
                    }
                  </Select>
                    </FormControl>
                 
            </Col>

            <Col xs={12} md={4}>
                  <FormControl fullWidth>
                  <Select
                    style={{width: "50%"}}
                    value={categorie}
                    onChange={(event)=> setCategorie(event.target.value)}
                  >
                    {
                      categories.map(categorie => <MenuItem key={categorie.id} value={categorie.id}> {categorie.name} </MenuItem>)
                    }
                  </Select>
                    </FormControl>
                 
            </Col>
            </Row>
          </div>
         
     


     
      </GridContainer>
      
    </div>
  );
}
