import React, { useState } from 'react';
import './Timeline.css'; // Import the CSS file for styling
import { IoMdCheckmarkCircleOutline } from "react-icons/io";

const hours = Array.from({ length: 24 }, (_, i) => i); // Array of hours from 0 to 23

const Timeline = ({ events }) => {
  const getRandomTop = () => {
    return Math.floor(Math.random() * 200); // Random number between 0 and 50
  };
  const [usedTopPositions, setUsedTopPositions] = useState([]); // Track used top positions

  const getUniqueTop = () => {
   return 100;
  };
  return (
    <div className="timeline-container">
      {/* Timeline Header */}
      <div className="timeline-header">
        {hours.map((hour) => (
          <div key={hour} className="timeline-hour">
            {hour}:00
          </div>
        ))}
      </div>

      {/* Timeline Events */}
      <div className="timeline-events">
        {events.map((event, index) => (
          <div
            key={index}
            className="timeline-event"
            style={{
              left: `${(event.startHour / 24) * 100}%`,
              // width: `${((event.endHour - event.startHour) / 12) * 100}%`,
              width:206,
              height:48,
              top: `${getUniqueTop()}px`,
              backgroundColor: event.color,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'left'
            }}
          >
            <div className='background-cercle'>
              <IoMdCheckmarkCircleOutline color='#FF6161' size={25} />  
           </div>   
            <div className='name-timeline'>
             <span>{event.name}</span>
             <span>{event.startHour} - {event.endHour}</span>
            </div>
          
          </div>
        ))}
      </div>
    </div>
  );
};

export default Timeline;
