import React, { useState } from 'react';
import './Timeline.css'; // Import the CSS file for styling
import { IoMdCheckmarkCircleOutline } from "react-icons/io";

const hours = Array.from({ length: 24 }, (_, i) => i); // Array of hours from 0 to 23

const Timeline = ({ events }) => {
  const [topEvents, setTopEvents] = useState(Array(events.length).fill(0)); // Store top positions for each event

  const getRandomTop = (index) => {
    const nextTop = Math.floor(Math.random() * 51); // Random number between 0 and 50
    setTopEvents((prev) => {
      const newTopEvents = [...prev];
      newTopEvents[index] = nextTop; // Update only the current index
      return newTopEvents;
    });
    return nextTop;
  };

  return (
    <div className="timeline-container">
      {/* Timeline Header */}
      <div className="timeline-header">
        {hours.map((hour) => (
          <div key={hour} className="timeline-hour">
            {hour}:00
          </div>
        ))}
      </div>

      {/* Timeline Events */}
      <div className="timeline-events">
        {events.map((event, index) => (
          <div
            key={index}
            className="timeline-event"
            style={{
              left: `${(event.startHour / 24) * 100}%`,
              width: '206px',
              height: '48px',
              top: `${topEvents[index] || 0}px`, // Use the top position from state
              backgroundColor: event.color,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'left'
            }}
            onMouseEnter={() => getRandomTop(index)} // Generate a new top position on hover
          >
            <div className='background-circle'>
              <IoMdCheckmarkCircleOutline color='#FF6161' size={25} />  
            </div>   
            <div className='name-timeline'>
              <span>{event.name}</span>
              <span>{event.startHour} - {event.endHour}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Timeline;
